integration_02:
node State {
    has name: str;
}

walker StateMachine {
    has current_state: str = "IDLE";

    can transition with entry {
        if self.current_state == "IDLE" {
            self.current_state = "PROCESSING";
            report "Transitioned to PROCESSING";
        } elif self.current_state == "PROCESSING" {
            self.current_state = "COMPLETED";
            report "Transitioned to COMPLETED";
        } else {
            report "Transition complete";
            disengage;
        }
        visit [-->];
    }
}

with entry {
    s1 = State(name="IDLE");
    s2 = State(name="PROCESSING");
    s3 = State(name="COMPLETED");

    s1 ++> s2 ++> s3;

    s1 spawn StateMachine();
}

