integration_05:
node Record {
    has id: int;
    has data: str;
}

walker CRUDWalker {
    has operation: str;
    has record_id: int = 0;
    has record_data: str = "";

    can create with entry {
        if self.operation == "create" {
            new_record = Record(id=self.record_id, data=self.record_data);
            :g:root ++> new_record;
            report f"Created record {self.record_id}";
        }
    }

    can read with entry {
        if self.operation == "read" {
            records = [-->(`?Record)];
            for rec in records {
                if rec.id == self.record_id {
                    report f"Found: {rec.data}";
                }
            }
        }
    }

    can update with entry {
        if self.operation == "update" {
            records = [-->(`?Record)];
            for rec in records {
                if rec.id == self.record_id {
                    rec.data = self.record_data;
                    report f"Updated record {self.record_id}";
                }
            }
        }
    }

    can delete with entry {
        if self.operation == "delete" {
            records = [-->(`?Record)];
            for rec in records {
                if rec.id == self.record_id {
                    del :g:root --> rec;
                    report f"Deleted record {self.record_id}";
                }
            }
        }
    }
}
